.btn--prev--preview,
.btn--next--preview {
  background-color: hsl(var(--clr-white));
  background-position: center;
  background-repeat: no-repeat;
  background-size: 25%;
  border-radius: 100vmax;
  padding: 1.1em;
  aspect-ratio: 1;
  position: absolute;
  top: 50%;
