.btn--next--preview {
  background-image: url("/e91c9665693cd0a88eac49fa00a4b5a1.svg");
  right: 1em;
}

.btn--close {
  position: absolute;
  top: 0;
  right: 32px;
  width: 20px;
  aspect-ratio: 1;
}
.btn--close:hover svg path {
  fill: hsl(var(--clr-accent));
}

.btn--prev--lightbox,
.btn--next--lightbox {
  background-color: #fff;
  border-radius: 100vmax;
  width: 50px;
  aspect-ratio: 1;
  position: absolute;
  top: 50%;
  display: grid;
  place-items: center;
}
.btn--prev--lightbox:hover svg path,
.btn--next--lightbox:hover svg path {
  stroke: hsl(var(--clr-accent));
}

.btn--prev--lightbox {
  left: -25px;
}

.btn--next--lightbox {
  right: -25px;
}

.btn--img--preview,
.btn--img--lightbox {
  border-radius: 0.5rem;
  background-size: cover;
  background-position: center;
  width: 100%;
  aspect-ratio: 1;
}
.btn--img--preview:hover,
.btn--img--lightbox:hover {
  opacity: 0.4;
}
.btn--img--preview:focus,
.btn--img--lightbox:focus {
  outline: 3px solid hsl(var(--clr-accent));
  border: none;
}
.btn--img--preview[aria-selected=true],
.btn--img--lightbox[aria-selected=true] {
  outline: 3px solid hsl(var(--clr-accent));
}

.btn--img--lightbox:hover {
  opacity: 1;
}

.btn--img--preview:nth-child(1),
.btn--img--lightbox:nth-child(1) {
  background-image: url("/401451cbd6a57dab7e8a846433e4c89c.jpg");
}

.btn--img--preview:nth-child(2),
.btn--img--lightbox:nth-child(2) {
  background-image: url("/c849838ffc6ac99621c306a409c18069.jpg");
}

.btn--img--preview:nth-child(3),
.btn--img--lightbox:nth-child(3) {
  background-image: url("/4e39ae2011ce20c4d81b1de48c09745b.jpg");
}

.btn--img--preview:nth-child(4),
.btn--img--lightbox:nth-child(4) {
  background-image: url("/08d02c2837aa92c5cf4c975355b9a73d.jpg");
}

.btn--minus,
.btn--plus {
  display: grid;
  place-items: center;
}
.btn--minus:hover svg use,
.btn--plus:hover svg use {
  fill: hsl(var(--clr-accent)/0.75);
}

.btn--add-to-cart {
  background-color: hsl(var(--clr-accent));
  border-radius: 0.75rem;
  padding: 1.1em;
  width: 100%;
  align-items: center;
  justify-content: center;
  box-shadow: hsl(var(--clr-accent)) 0px 8px 35px -10px;
}
.btn--add-to-cart:hover {
  background-color: hsl(var(--clr-accent)/0.75);
}
.btn--add-to-cart:active {
  transform: translateY(1.2px);
}

.btn--checkout {
  background-color: hsl(var(--clr-accent));
  border-radius: 0.75rem;
  padding: 1.1em;
  width: 100%;
}
.btn--checkout:hover {
  background-color: hsl(var(--clr-accent)/0.75);
}
.btn--checkout:active {
  transform: translateY(1.2px);
}

.cart-container {
  align-items: center;
}
.cart-container__pfp {
  cursor: pointer;
  max-width: 25px;
}
.cart-container__pfp:hover {
  opacity: 0.7;
}
