  .cart-container {
    margin-left: auto;
    position: relative;
  }
  .cart-container__pfp {
    max-width: 50px;
  }
}
@media (min-width: 45em) and (min-width: 55em) {
  .cart-container__pfp {
    margin-left: 1.85rem;
  }
}

.cart {
  z-index: 50;
  padding: 1.4em 1.3em;
  border-radius: 0.75rem;
  position: absolute;
  background-color: hsl(var(--clr-white));
  box-shadow: rgba(0, 0, 0, 0.2) 0px 25px 25px 0px;
  inset: 75px 7px auto 7px;
  display: none;
}
@media (min-width: 40em) {
  .cart {
    min-width: 360px;
    inset: 60px 50px auto auto;
  }
}
.cart__title::after {
  content: "";
  position: absolute;
  top: 65px;
  left: 0;
  background-color: hsl(var(--clr-primary-600)/0.4);
  width: 100%;
  height: 2px;
}
.cart__content {
  height: 100%;
  padding: 4em 0 1em;
  gap: 1rem;
}
.cart__content__p {
  text-align: center;
}
.cart__content .btn {
  align-self: start;
}
.cart__content__info {
  align-items: center;
  justify-self: start;
}
.cart__content__details {
  flex-direction: column;
  align-items: flex-start;
  gap: 0.4rem;
}
.cart__content__details strong {
  color: hsl(var(--clr-black));
}
.cart__content__img {
  max-width: 50px;
  border-radius: 0.25rem;
}
.cart i {
  margin-left: auto;
  margin-right: 0rem;
}
.cart[data-visible=true] {
  display: block;
}

.nav__list {
  padding: 5.5em 1.4em;
  flex-direction: column;
}
@media (min-width: 40em) {
  .nav__list {
    padding: 0;
    flex-direction: row;
    gap: min(2vw, 2.25rem);
    color: hsl(var(--clr-primary-700));
  }
  .nav__item {
    position: relative;
  }
  .nav__item:hover {
    color: hsl(var(--clr-primary-800));
  }
  .nav__item:hover::after {
    content: "";
    position: absolute;
    bottom: -44px;
    left: 0;
    background-color: hsl(var(--clr-accent));
    width: 100%;
    height: 5px;
  }
}

@media (max-width: 40em) {
  .primary-nav {
    position: fixed;
    inset: 0 32.5% 0 0;
    font-weight: 700;
    background-color: hsl(var(--clr-white));
    z-index: 300;
    transform: translateX(-100%);
    transition: transform 0.5s ease-in-out;
  }
  .primary-nav[data-visible=true] {
    transform: translateX(0);
  }
}
.lightbox {
  border: 0;
  background: transparent;
  max-width: 530px;
  z-index: 200;
  padding: 2em;
}
.lightbox__imgs {
  position: relative;
}
.lightbox__imgs__img {
  border-radius: 0.75rem;
}
.lightbox__imgs__img[data-visible=false] {
  display: none;
}
.lightbox__imgs__img[data-visible=true] {
  display: block;
}
.lightbox__img-btns {
  gap: 1.5rem;
  width: 80%;
  margin-inline: auto;
  margin-top: 2rem;
}

.lightbox::backdrop {
  background: rgba(0, 0, 0, 0.8);
}

.content {
  --flow-space: 1rem;
  padding: 1em 1.5em;
}
.content__subtitle {
  line-height: 1.1;
}
.content__p {
  max-width: 50ch;
}
.content .price {
  justify-content: space-between;
  align-items: center;
}
.content .price__discount {
  padding: 0 0.4em;
  border-radius: 0.5rem;
  margin-left: 0.5rem;
}
.content .price__num {
  align-items: center;
  gap: 0.2rem;
}
.content .cta {
  gap: 1.25rem;
  flex-direction: column;
}
.content .cta__qty {
  padding: 1em;
  justify-content: space-between;
  border-radius: 0.75rem;
}
@media (min-width: 35em) {
  .content {
    padding: 1em 0;
  }
  .content .cta {
    flex-direction: row;
  }
  .content .cta > *:first-child {
    width: 55%;
  }
}
@media (min-width: 45em) {
  .content {
    max-width: 50ch;
    --flow-space: 1.25rem;
  }
  .content .price {
    flex-direction: column;
    align-items: flex-start;
    gap: 0;
  }
}

.imgs .img-btns {
  gap: 1rem;
  margin-top: 1.25rem;
}
@media (max-width: 35em) {
  .imgs .img-btns {
    display: none;
  }
}
.imgs .preview-imgs {
  position: relative;
}
.imgs .preview-imgs__img {
  cursor: pointer;
}
.imgs .preview-imgs__img[data-visible=false] {
  display: none;
}
.imgs .preview-imgs__img[data-visible=true] {
  display: block;
}
@media (min-width: 35em) {
  .imgs {
    max-width: 80%;
  }
  .imgs img {
    border-radius: 0.75rem;
  }
}
@media (min-width: 45em) {
  .imgs {
    max-width: 70%;
  }
}

